CREATE TABLE public."Remarks" (
	"Id" int4 NOT NULL GENERATED BY DEFAULT AS IDENTITY,
	"CreatedDate" timestamptz NULL,
	"ModifiedDate" timestamptz NULL,
	"SystemInfo" varchar(50) NULL,
	"IsActive" bool NULL,
	"CreatedBy" varchar(100) NULL,
	"ModifiedBy" varchar(100) NULL,
	"AssociateExitId" int4 NOT NULL,
	"RoleId" int4 NOT NULL,
	"Comment" text NULL,
	CONSTRAINT "PK_Remarks" PRIMARY KEY ("Id")
);
CREATE INDEX "IX_Remarks_AssociateExitId" ON public."Remarks" USING btree ("AssociateExitId");


-- public."Remarks" foreign keys

ALTER TABLE public."Remarks" ADD CONSTRAINT "FK_Remarks_AssociateExit_AssociateExitId" FOREIGN KEY ("AssociateExitId") REFERENCES public."AssociateExit"("AssociateExitId") ON DELETE CASCADE;